function deepClone(obj, map = new WeakMap()) {
  if (obj === null || typeof obj !== 'object') return obj;
  if (map.has(obj)) return map.get(obj);

  const clone = Array.isArray(obj) ? [] : {};
  map.set(obj, clone);

  for (let key in obj) {
    if (obj.hasOwnProperty(key)) {
      clone[key] = deepClone(obj[key], map);
    }
  }

  return clone;
}

class SimplePromise {
  constructor(executor) {
    this.state = 'pending'
    this.value = undefined
    this.callbacks = []

    const resolve = value => {
      if (this.state !== 'pending') return

      // å¤„ç† promise è¿”å› promise çš„æƒ…å†µ
      if (value instanceof SimplePromise) {
        return value.then(resolve, reject)
      }

      this.state = 'fulfilled'
      this.value = value
      this.callbacks.forEach(cb => this._handle(cb))
    }

    const reject = reason => {
      if (this.state !== 'pending') return

      this.state = 'rejected'
      this.value = reason
      this.callbacks.forEach(cb => this._handle(cb))
    }

    // âœ… ä¿®å¤ï¼šç”¨ try-catch åŒ…è£¹ executor
    try {
      executor(resolve, reject)
    } catch (error) {
      reject(error)  // æ•è· executor ä¸­çš„åŒæ­¥é”™è¯¯
    }
  }

  then(onFulfilled, onRejected) {
    onFulfilled = typeof onFulfilled === 'function' ? onFulfilled : value => value
    onRejected = typeof onRejected === 'function' ? onRejected : reason => { throw reason }

    return new SimplePromise((resolve, reject) => {
      this._pushCallback({
        onFulfilled,
        onRejected,
        resolve,
        reject
      })
    })
  }

  catch(onRejected) {
    return this.then(null, onRejected)
  }

  _pushCallback(callback) {
    if (this.state === 'pending') {
      this.callbacks.push(callback)
    } else {
      setTimeout(() => this._handle(callback), 0)
    }
  }

  _handle(callback) {
    const { onFulfilled, onRejected, resolve, reject } = callback

    try {
      if (this.state === 'fulfilled') {
        const result = onFulfilled(this.value)
        resolve(result)
      } else if (this.state === 'rejected') {
        const result = onRejected(this.value)
        resolve(result)
      }
    } catch (error) {
      reject(error)  // æ•è·å›è°ƒå‡½æ•°ä¸­çš„é”™è¯¯
    }
  }
}

